#!/usr/bin/env bash
# Script to create GitHub issues for ALL open and assigned tasks
# Generated by Planning Petra on 2025-11-26
# Organizes tasks into logical epics based on context and priority

set -euo pipefail

REPO="sddevelopment-be/quickstart_agent-augmented-development"
SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
REPO_ROOT="$(cd "$SCRIPT_DIR/.." && pwd)"

echo "ğŸ—ï¸  Creating GitHub issues for all open/assigned tasks..."
echo ""

# Function to create an epic
create_epic() {
  local title="$1"
  local body="$2"
  local labels="${3:-epic,orchestration}"
  
  echo "ğŸ“‹ Creating Epic: $title..."
  
  local epic_number=$(bash "$REPO_ROOT/ops/scripts/create-github-issue.sh" \
    --repo "$REPO" \
    --title "$title" \
    --label "$labels" \
    --body "$body" 2>&1 | grep -oP '#\K[0-9]+' | head -1 || echo "")
  
  if [ -z "$epic_number" ]; then
    echo "âš ï¸  Could not extract epic number, continuing..."
    epic_number="UNKNOWN"
  else
    echo "âœ… Epic created: #$epic_number"
  fi
  
  echo "$epic_number"
}

# Function to create an issue
create_issue() {
  local title="$1"
  local body="$2"
  local labels="$3"
  local epic_ref="$4"
  
  echo "ğŸ“ Creating: $title"
  
  # Add epic reference if provided
  if [ -n "$epic_ref" ] && [ "$epic_ref" != "UNKNOWN" ]; then
    body="${body}

---

**Part of Epic:** #${epic_ref}"
  fi
  
  bash "$REPO_ROOT/ops/scripts/create-github-issue.sh" \
    --repo "$REPO" \
    --title "$title" \
    --label "$labels" \
    --body "$body" || echo "âš ï¸  Failed to create: $title"
  
  echo ""
}

